import React from "react";

function App() {
    const [contact, setContact] = React.useState(
        {
            firstName: "akariae",
            lastName:"Mahi",
            phone:"+212601535299",
            email:"mahi.zakariae@gmail.com",
            isFavorite: false
        }
    );
    return (
        <article className="card container">
            <img className="card--image" src="avatar.png" alt="" />
            <div className="card--info">
                <i className="fa-regular fa-star review--icon"></i>
                <i className="fa-solid fa-star review--icon gold"></i>
                <h2 className="card--name">{contact.firstName} {contact.lastName}</h2>
                <h3 className="card--phone">+212601535299</h3>
                <h3 className="card--email">Mahi.zakariae@gmail.com</h3>
            </div>
        </article>
    );
}

export default App;
